var onoff = require('onoff').Gpio;
var Q = require('q');

module.exports = function button(number) {
  var button = new onoff(number, 'in', 'falling');

  return {

    setListener: function(callback) {
      button.watch(callback);
    },

    unexport: function() {
      res = button.unexport();
    }
  }
}
