var print = function(io) {
  var socket = io;
  socket.on('connection', function(socket) {
    socket.on('join', function(data) {
      console.log(data);
      socket.emit('messages', 'ping from server');
    });
  });

  return {

    message: function(data) {
      socket.emit('messages', data);
    }
  }
}

module.exports = print;
